// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

contract BackupContract {
    mapping(address => string) private backups;
    string private latestBackup;
    
    event BackupStored(address indexed user, string ipfsHash);
    
    function storeBackup(string memory ipfsHash) public {
        backups[msg.sender] = ipfsHash;
        latestBackup = ipfsHash;
        emit BackupStored(msg.sender, ipfsHash);
    }
    
    function getBackupFor(address user) public view returns (string memory) {
        return backups[user];
    }
    
    function getBackup() public view returns (string memory) {
        return latestBackup;
    }
}
